import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import { Container, Form, Button, Row, Col, Alert } from 'react-bootstrap';

function App() {
  const [date, setDate] = useState('');
  const [smallDogs, setSmallDogs] = useState(0);
  const [largeDogs, setLargeDogs] = useState(0);
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/best-petshop', {
        date,
        smallDogs: parseInt(smallDogs),
        largeDogs: parseInt(largeDogs),
      });
      setResult(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <Container className="mt-5">
      <h1 className="text-center">Encontre o Melhor Petshop</h1>
      <Form onSubmit={handleSubmit} className="mt-4">
        <Form.Group as={Row} controlId="formDate">
          <Form.Label column sm={2}>
            Data:
          </Form.Label>
          <Col sm={10}>
            <Form.Control
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formSmallDogs">
          <Form.Label column sm={2}>
            Cães Pequenos:
          </Form.Label>
          <Col sm={10}>
            <Form.Control
              type="number"
              value={smallDogs}
              onChange={(e) => setSmallDogs(e.target.value)}
              required
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formLargeDogs">
          <Form.Label column sm={2}>
            Cães Grandes:
          </Form.Label>
          <Col sm={10}>
            <Form.Control
              type="number"
              value={largeDogs}
              onChange={(e) => setLargeDogs(e.target.value)}
              required
            />
          </Col>
        </Form.Group>

        <Button variant="primary" type="submit" className="mt-3">
          Encontrar Melhor Petshop
        </Button>
      </Form>

      {result && (
        <Alert variant="success" className="mt-4">
          <h4>Melhor Petshop: {result.petshop}</h4>
          <p>Preço Total: R${result.price}</p>
        </Alert>
      )}
    </Container>
  );
}

export default App;
